import React from 'react';
import { View, Text, StyleSheet,Image } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

export default function Header() {
  return (
    <View>
    <View style={styles.headerContainer}>
      <View style={styles.textSearchContainer}>
        <Icon name="search" size={40} color="black" />
        <Text style={styles.textsearch}> Tìm kiếm</Text>
        
      </View>
      <View><Icon name="search" size={40} color="black" /></View>
    </View>
    </View>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    borderWidth: 2,
    borderColor: 'black',
    borderRadius: 30,
    padding: 10,
    margin: 20,
    justifyContent: 'space-around', // Các icon được căn đều trên hàng
  },
  textSearchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    
  },
  textsearch: {
   
    paddingTop: 10,
    paddingBottom: 10,
    paddingRight: 20,
    paddingLeft: 20,
    marginRight: 10, 
  },
  textContainer: {
    fontSize: 20,
    fontWeight: 'bold',
    textAlign: 'center',
    paddingTop:40
  
  },
});
