import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';


export default function Header() {
  return (
    <View style={styles.headercontainer}>
      <Text style={styles.uppercaseText}>Trà Sữa Nhà Làm</Text>
      <StatusBar style="auto" />
    </View>
  );
}

<View style={styles.container}>
      <TextInput
        style={styles.input}
        placeholder="Search..."
        placeholderTextColor="#fff" // Màu chữ khi không có nội dung
        onChangeText={(text) => setSearchText(text)}
        value={searchText}
      />
      <Icon name="search" size={20} color="#fff" onPress={handleSearch} />
    </View>
const styles = StyleSheet.create({
  headercontainer: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  uppercaseText: {
    textTransform: 'uppercase',
  },
});
