import React, { useState } from 'react';
import { View, TextInput, StyleSheet } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

const SearchBar = () => {
  const [searchText, setSearchText] = useState('');

  const handleSearch = () => {
    // Xử lý tìm kiếm ở đây, bạn có thể gọi API hoặc thực hiện logic tìm kiếm
    console.log('Searching for:', searchText);
  };

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.input}
        placeholder="Search..."
        placeholderTextColor="#fff" // Màu chữ khi không có nội dung
        onChangeText={(text) => setSearchText(text)}
        value={searchText}
      />
      <Icon name="search" size={20} color="#fff" onPress={handleSearch} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#000', // Màu nền của khung tìm kiếm
    padding: 10,
    borderRadius: 5,
    borderColor: '#000', // Màu viền
    borderWidth: 1, // Độ dày của viền
  },
  input: {
    flex: 1,
    color: '#000', // Màu chữ khi nhập liệu
    marginRight: 10,
  },
});

export default SearchBar;
