import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';

const SearchBar = () => {
  const [searchText, setSearchText] = useState('');

  const handleSearch = () => {
    // Xử lý tìm kiếm ở đây, bạn có thể gọi API hoặc thực hiện logic tìm kiếm
    console.log('Searching for:', searchText);
  };

  return (
    <View style={styles.headercontainer}>
      <Text style={styles.uppercaseText}>Trà Sữa Nhà Làm</Text>
      <StatusBar style="auto" />
    </View>
  );
}


const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#000', // Màu nền của khung tìm kiếm
    padding: 10,
    borderRadius: 5,
    borderColor: '#000', // Màu viền
    borderWidth: 1, // Độ dày của viền
  },
  input: {
    flex: 1,
    color: '#000', // Màu chữ khi nhập liệu
    marginRight: 10,
  },
});

export default SearchBar;
