import React from 'react';
import { View, StyleSheet } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

const Footer = () => {
  return (
    <View style={styles.footerContainer}>
      <Icon name="home" size={30} color="black" style={styles.icon} />
      <Icon name="heart" size={30} color="red" style={styles.icon} />
      <Icon name="user" size={30} color="blue" style={styles.icon} />
      {/* Thêm các icon khác nếu cần */}
    </View>
  );
};

const styles = StyleSheet.create({
  footerContainer: {
    flexDirection: 'row', // Hiển thị các icon cùng một hàng
    justifyContent: 'space-around', // Các icon được căn đều trên hàng
    padding: 10,
    backgroundColor: 'lightgray', // Màu nền của footer
  },
  icon: {
    marginRight: 20, // Khoảng cách giữa các icon
  },
});

export default Footer;
